const earthTilt = 23.4 * Math.PI / 180; // tilt in radians
const earthAxis = new THREE.Vector3(Math.sin(earthTilt), Math.cos(earthTilt), 0).normalize();

const sunRadius = 0.00465047;
const sunRadiusKM = 1391400;

function getScaledDistanceFromSun(planetDistanceAU) {
  var realScale = (planetDistanceAU * 10) / sunRadius;
  return (realScale / 100) + 10;
}

function getScaledSize(planetSizeKM) {
  var realScale = (planetSizeKM * 10) / sunRadiusKM;
  return realScale * 8;
}

window.planetSizes = {
  sun: 10,
  mercury: getScaledSize(4879),
  venus: getScaledSize(12104),
  earth: getScaledSize(12756),
  mars: getScaledSize(6792),
  jupiter: getScaledSize(142984),
  saturn: getScaledSize(120536),
  uranus: getScaledSize(51118),
  neptune: getScaledSize(49528)
}

window.planetDistance = {
  mercury: getScaledDistanceFromSun(.4),
  venus: getScaledDistanceFromSun(.7),
  earth: getScaledDistanceFromSun(1),
  mars: getScaledDistanceFromSun(1.5),
  jupiter: getScaledDistanceFromSun(5.2),
  saturn: getScaledDistanceFromSun(9.6),
  uranus: getScaledDistanceFromSun(19.2),
  neptune: getScaledDistanceFromSun(30),
  pluto: getScaledDistanceFromSun(39.5)
}

function backgroundSoundInit() {
  const listener = new THREE.AudioListener();
  window.GLOBAL_GL.camera.add( listener );
  window.GLOBAL_GL.jonObjects.sound = new THREE.Audio( listener );
  const audioLoader = new THREE.AudioLoader();
  audioLoader.load( './sounds/ambience.mp3', function( buffer ) {
    window.GLOBAL_GL.jonObjects.sound.setBuffer( buffer );
    window.GLOBAL_GL.jonObjects.sound.setLoop( true );
    window.GLOBAL_GL.jonObjects.sound.setVolume( 0.2 );
  });

  const playPauseButton = document.createElement('button');
  playPauseButton.className = 'play-pause';
  playPauseButton.innerText = window.GLOBAL_GL.jonObjects.sound.isPlaying ? 'Pause Ambient Sound' : 'Play Ambient Sound';
  playPauseButton.addEventListener('click', playPause);
  playPauseButton.style = 'position: absolute; left: 10px; top: 10px;';
  document.querySelector('body').appendChild(playPauseButton);
}

function playPause() {
  const { sound } = window.GLOBAL_GL.jonObjects
  if (sound.isPlaying) {
    sound.pause();
    document.querySelector('.play-pause').innerText = 'Play Ambient Sound';
  } else {
    sound.play();
    document.querySelector('.play-pause').innerText = 'Pause Ambient Sound';
  }

}

