function setInOrbit(planetName, earthYears, rotationSpeed, time) {
  const speed = (365 / earthYears) * window.GLOBAL_GL.jonObjects.ORBIT_SPEED;
  if (planetName === 'earth') {
    window.GLOBAL_GL.jonObjects[planetName].rotateOnAxis(earthAxis, rotationSpeed);
  } else {
    window.GLOBAL_GL.jonObjects[planetName].rotation.x += rotationSpeed;
    window.GLOBAL_GL.jonObjects[planetName].rotation.y += rotationSpeed;
  }
  window.GLOBAL_GL.jonObjects[planetName].position.x = Math.cos(time * speed) * window.planetDistance[planetName];
  window.GLOBAL_GL.jonObjects[planetName].position.y = Math.sin(time * speed) * window.planetDistance[planetName];
  // window.GLOBAL_GL.jonObjects[planetName].position.z = Math.sin(time * speed) * distance;
}

function jonRenderer() {
  const time = Date.now();
  setInOrbit('mercury', 0.24, 0.005, time);
  setInOrbit('venus', 0.62, 0.005, time);
  setInOrbit('earth', 1, 0.001, time);
  setInOrbit('mars', 1.8, 0.004, time)
  setInOrbit('jupiter', 11.87, 0.004, time)
  setInOrbit('saturn', 29.48, 0.004, time)
  setInOrbit('uranus', 84.07, time)
  setInOrbit('neptune', 164.90, time)
  setInOrbit('pluto', 247.78, time)
}
